import logo from './logo.svg';
import 'bulma/css/bulma.min.css';
import './App.css';
import headPic from './Stick.jpg';
import { useState, useEffect } from 'react';
import ReactImageAppear from 'react-image-appear';

function App() {

  const [submitType, updateType] = useState('Players');
  const [color1, updateColor1] = useState('typeButton');
  const [color2, updateColor2] = useState('typeButton');

  return (
    <div>


      <div className="section">
        <div className="container">
          <h1>Hockey Comparison Tool<img className="headPic" src={headPic} alt="headPic" /></h1>
          <br />
          <br />
          <h2>Compare: <button className={color1} onClick={() => { updateType('Players'); updateColor1('typeButton2'); updateColor2('typeButton') }}><h2>Players</h2></button> <button className={color2} onClick={() => { updateType('Teams'); updateColor2('typeButton2'); updateColor1('typeButton') }}><h2>Teams</h2></button></h2>
          <button onClick={() => {updateType('3rd')}}></button>


        </div>
      </div>
      <Forms type={submitType} />
    </div>
  );
}

function Forms(props) {




  const [formCount, addCount] = useState(2);
  const [form1Option1, updateF3O1] = useState('option 2');
  const [formType, updateType] = useState(props.type);
  const [formObjs, updateObjs] = useState([{ prop1: '1', prop2: '2' }, { prop3: '3', prop4: '4' }])


  //////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////    1

  const [season1, updateSeason1] = useState('');
  const [teams1, updateTeams1] = useState([]);
  const [selectedTeam1, updateSelectedTeam1] = useState(null);
  const [players1, updatePlayers1] = useState([]);
  const [selectedPlayer1, updateSelectedPlayer1] = useState(null);
  const [player1Img, updatePlayer1Img] = useState(null);

  if (season1.length === 0) {
    updateSeason1('2020-2021');
  }

  //update teams1
  useEffect(() => {
    let fixedSeason = season1.replace(/-/g, "");
    async function fetchData() {



      await fetch(`https://statsapi.web.nhl.com/api/v1/teams?season=${fixedSeason}`, {
        method: "GET",


      })
        .then(res => res.json())
        .then(
          (result) => {

            updateTeams1(result.teams.sort(function (a, b) {
              var nameA = a.name.toLowerCase(), nameB = b.name.toLowerCase()
              if (nameA < nameB) //sort string ascending
                return -1
              if (nameA > nameB)
                return 1
              return 0 //default return value (no sorting)
            }));


          },


        )
    }

    fetchData();
    return;
  }, [season1]);



  //update players1

  useEffect(() => {  


    let teamSelected = teams1.find(function isTeam(team) {
      return team.name === selectedTeam1;
    })

    if(teamSelected === undefined){
      teamSelected = {id: 24};
    }

    let fixedSeason = season1.replace(/-/g, "");

    async function fetchData() {



      await fetch(`https://statsapi.web.nhl.com/api/v1/teams/${teamSelected.id}?expand=team.roster&season=${fixedSeason}`, {
        method: "GET",


      })
        .then(res => res.json())
        .then(
          (result) => {

            updatePlayers1(

              result.teams[0].roster.roster.sort(function (a, b) {
                var nameA = a.person.fullName.toLowerCase(), nameB = b.person.fullName.toLowerCase()
                if (nameA < nameB) //sort string ascending
                  return -1
                if (nameA > nameB)
                  return 1
                return 0 //default return value (no sorting)
              }));



          },


        )
    }

    fetchData();
    return;
  }, [selectedTeam1, season1, teams1]);

  ////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////    2

  const [season2, updateSeason2] = useState('');
  const [teams2, updateTeams2] = useState([]);
  const [selectedTeam2, updateSelectedTeam2] = useState(null);
  const [players2, updatePlayers2] = useState([]);
  const [selectedPlayer2, updateSelectedPlayer2] = useState(null);

  console.log(season2.length);
  if (season2.length === 0) {
    updateSeason2('2020-2021');
  }

  //update teams2
  useEffect(() => {
    let fixedSeason = season2.replace(/-/g, "");
    async function fetchData() {



      await fetch(`https://statsapi.web.nhl.com/api/v1/teams?season=${fixedSeason}`, {
        method: "GET",


      })
        .then(res => res.json())
        .then(
          (result) => {

            updateTeams2(result.teams.sort(function (a, b) {
              var nameA = a.name.toLowerCase(), nameB = b.name.toLowerCase()
              if (nameA < nameB) //sort string ascending
                return -1
              if (nameA > nameB)
                return 1
              return 0 //default return value (no sorting)
            }));


          },


        )
    }

    fetchData();
    return;
  }, [season2]);



  //update players2

  useEffect(() => {  


    let teamSelected = teams2.find(function isTeam(team) {
      return team.name === selectedTeam2;
    })

    if(teamSelected === undefined){
      teamSelected = {id: 24};
    }

    let fixedSeason = season2.replace(/-/g, "");

    async function fetchData() {



      await fetch(`https://statsapi.web.nhl.com/api/v1/teams/${teamSelected.id}?expand=team.roster&season=${fixedSeason}`, {
        method: "GET",


      })
        .then(res => res.json())
        .then(
          (result) => {

            updatePlayers2(

              result.teams[0].roster.roster.sort(function (a, b) {
                var nameA = a.person.fullName.toLowerCase(), nameB = b.person.fullName.toLowerCase()
                if (nameA < nameB) //sort string ascending
                  return -1
                if (nameA > nameB)
                  return 1
                return 0 //default return value (no sorting)
              }));



          },


        )
    }

    fetchData();
    return;
  }, [selectedTeam2, season2, teams2]);






  //////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////// 3
  const [season3, updateSeason3] = useState('');
  const [teams3, updateTeams3] = useState([]);
  const [selectedTeam3, updateSelectedTeam3] = useState(null);
  const [players3, updatePlayers3] = useState([]);
  const [selectedPlayer3, updateSelectedPlayer3] = useState(null);

  if (season3.length === 0) {
    updateSeason3('2020-2021');
  }

  //update teams3
  useEffect(() => {
    let fixedSeason = season3.replace(/-/g, "");
    async function fetchData() {



      await fetch(`https://statsapi.web.nhl.com/api/v1/teams?season=${fixedSeason}`, {
        method: "GET",


      })
        .then(res => res.json())
        .then(
          (result) => {

            updateTeams3(result.teams.sort(function (a, b) {
              var nameA = a.name.toLowerCase(), nameB = b.name.toLowerCase()
              if (nameA < nameB) //sort string ascending
                return -1
              if (nameA > nameB)
                return 1
              return 0 //default return value (no sorting)
            }));


          },


        )
    }

    fetchData();
    return;
  }, [season3]);



  //update players3

  useEffect(() => {  


    let teamSelected = teams3.find(function isTeam(team) {
      return team.name === selectedTeam3;
    })

    if(teamSelected === undefined){
      teamSelected = {id: 24};
    }

    let fixedSeason = season3.replace(/-/g, "");

    async function fetchData() {



      await fetch(`https://statsapi.web.nhl.com/api/v1/teams/${teamSelected.id}?expand=team.roster&season=${fixedSeason}`, {
        method: "GET",


      })
        .then(res => res.json())
        .then(
          (result) => {

            updatePlayers3(

              result.teams[0].roster.roster.sort(function (a, b) {
                var nameA = a.person.fullName.toLowerCase(), nameB = b.person.fullName.toLowerCase()
                if (nameA < nameB) //sort string ascending
                  return -1
                if (nameA > nameB)
                  return 1
                return 0 //default return value (no sorting)
              }));



          },


        )
    }

    fetchData();
    return;
  }, [selectedTeam3, season3, teams3]);


////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////// 4

const [season4, updateSeason4] = useState('');
const [teams4, updateTeams4] = useState([]);
const [selectedTeam4, updateSelectedTeam4] = useState(null);
const [players4, updatePlayers4] = useState([]);
const [selectedPlayer4, updateSelectedPlayer4] = useState(null);

console.log(season4.length);
if (season4.length === 0) {
  updateSeason4('2020-2021');
}

//update teams4
useEffect(() => {
  let fixedSeason = season4.replace(/-/g, "");
  async function fetchData() {



    await fetch(`https://statsapi.web.nhl.com/api/v1/teams?season=${fixedSeason}`, {
      method: "GET",


    })
      .then(res => res.json())
      .then(
        (result) => {

          updateTeams4(result.teams.sort(function (a, b) {
            var nameA = a.name.toLowerCase(), nameB = b.name.toLowerCase()
            if (nameA < nameB) //sort string ascending
              return -1
            if (nameA > nameB)
              return 1
            return 0 //default return value (no sorting)
          }));


        },


      )
  }

  fetchData();
  return;
}, [season4]);



//update players4

useEffect(() => {  


  let teamSelected = teams4.find(function isTeam(team) {
    return team.name === selectedTeam4;
  })

  if(teamSelected === undefined){
    teamSelected = {id: 24};
  }

  let fixedSeason = season4.replace(/-/g, "");

  async function fetchData() {



    await fetch(`https://statsapi.web.nhl.com/api/v1/teams/${teamSelected.id}?expand=team.roster&season=${fixedSeason}`, {
      method: "GET",


    })
      .then(res => res.json())
      .then(
        (result) => {

          updatePlayers4(

            result.teams[0].roster.roster.sort(function (a, b) {
              var nameA = a.person.fullName.toLowerCase(), nameB = b.person.fullName.toLowerCase()
              if (nameA < nameB) //sort string ascending
                return -1
              if (nameA > nameB)
                return 1
              return 0 //default return value (no sorting)
            }));



        },


      )
  }

  fetchData();
  return;
}, [selectedTeam4, season4, teams4]);


  if (formType !== props.type) {
    updateType(props.type);
    addCount(2);

    let updatedObj = formObjs.map(item => {

      if (item.prop1 === '1') {
        return { ...item, prop1: '5' };
      }

      return item;
    })

    updateObjs(updatedObj);
  }




  if (formType === 'Players') {



    return (


      <section>
        <div className='container has-background-grey-light'>
          <div className="columns">
            <div className="column">
              <form>
                <h2>Player 1</h2>
                <br />
                <label>Season: </label>
                <select onChange={evt => updateSeason1(evt.target.value)} name="selectList" id="selectList">
                  <option value="2020-2021">2020-2021</option>
                  <option value="2019-2020">2019-2020</option>
                  <option value="2018-2019">2018-2019</option>
                  <option value="2017-2018">2017-2018</option>
                  <option value="2016-2017">2016-2017</option>
                </select>

                <br></br>
                <br />

                <label>Team: </label>
                <select onChange={evt => updateSelectedTeam1(evt.target.value)} name="selectList" id="selectList">
                  {teams1.map(team => (
                    <option>{team.name}</option>
                  ))}
                </select>

                <br></br>
                <br />

                <label>Player: </label>
                <select value = {selectedPlayer1} onChange={evt => updateSelectedPlayer1(evt.target.value)} name="selectList" id="selectList">
                  {players1.map(player => (
                    <option>{player.person.fullName}</option>
                  ))}
                </select>

              </form>

            </div>
            <div className="column">
              <form>
                <h2>Player 2</h2>
                <br />
                <label>Season: </label>
                <select onChange={evt => updateSeason2(evt.target.value)} name="selectList" id="selectList">
                  <option value="2020-2021">2020-2021</option>
                  <option value="2019-2020">2019-2020</option>
                  <option value="2018-2019">2018-2019</option>
                  <option value="2017-2018">2017-2018</option>
                  <option value="2016-2017">2016-2017</option>
                </select>

                <br></br>
                <br />

                <label>Team: </label>
                <select onChange={evt => updateSelectedTeam2(evt.target.value)} name="selectList" id="selectList">
                  {teams2.map(team => (
                    <option>{team.name}</option>
                  ))}
                </select>

                <br></br>
                <br />

                <label>Player: </label>
                <select value = {selectedPlayer2} onChange={evt => updateSelectedPlayer2(evt.target.value)} name="selectList" id="selectList">
                  {players2.map(player => (
                    <option>{player.person.fullName}</option>
                  ))}
                </select>


              </form>

            </div>
            <div className="column">
              <Player3 />
            </div>
            <div className="column">
              <Player4 />
            </div>
          </div>

          <button className="submitButton">Submit</button>
        </div>
      </section>
    )

  } else if (formType === 'Teams'){
    console.log(formType);

    return (
      <section>
        <div className='container has-background-grey-light'>
          <div className="columns">
            <div className="column">
              <form>
                <h2>Team 1</h2>
                <br />
                <label>Season: </label>
                <select name="selectList" id="selectList">
                  <option value="option 1">Option 1</option>
                  <option value="option 2">Option 2</option>
                </select>

                <br></br>
                <br />

                <label>Team: </label>
                <select name="selectList" id="selectList">
                  <option value="option 1">Option 1</option>
                  <option value="option 2">Option 2</option>
                </select>

                <br></br>
                <br />

              </form>

            </div>
            <div className="column">
              <form>
                <h2>Team 2</h2>
                <br />
                <label>Season: </label>
                <select name="selectList" id="selectList">
                  <option value="option 1">Option 1</option>
                  <option value="option 2">Option 2</option>
                </select>

                <br></br>
                <br />

                <label>Team: </label>
                <select name="selectList" id="selectList">
                  <option value="option 1">Option 1</option>
                  <option value="option 2">Option 2</option>
                </select>

                <br></br>
                <br />
              </form>

            </div>
            <div className="column">
              <Player3 />
            </div>
            <div className="column">
              <Player4 />
            </div>
          </div>

          <button className="submitButton">Submit</button>
        </div>
      </section>

    )


  } else {

    const useImage = (src) => {
      const [hasLoaded, setHasLoaded] = useState(false);
      const [hasError, setHasError] = useState(false);
      const [hasStartedInitialFetch, setHasStartedInitialFetch] = useState(false);

      useEffect(() => {
          setHasStartedInitialFetch(true);
          setHasLoaded(false);
          setHasError(false);

          // Here's where the magic happens.
          const image = new Image();
          image.src = src;

          const handleError = () => {
              setHasError(true);
          };

          const handleLoad = () => {
              setHasLoaded(true);
              setHasError(false);
          };

          image.onerror = handleError;
          image.onload = handleLoad;

          return () => {
              image.removeEventListener("error", handleError);
              image.removeEventListener("load", handleLoad);
          };
      }, [src]);


      const Demo = () => {
        const imageUrl = "https://nhl.bamcontent.com/images/headshots/current/168x168/8474600.jpg";

        const { hasLoaded, hasError } = useImage(imageUrl);

        if (hasError) {
            return null;
        }

        return (
            <>
                ...
                {!hasLoaded && <LoadingIndicator />}
                {hasLoaded && <img src={imageUrl} />}
            </>
        );
    };

    Demo();


  };



    useImage('https://nhl.bamcontent.com/images/headshots/current/168x168/8474600.jpg')




  }

  function Player3() {

    if (formCount <= 2) {
      return (
        <p className="addClick" onClick={() => {
          addCount(formCount + 1)
        }}>+</p>
      )
    } else {

      if (props.type === "Players") {

        return (<form>
          <h2>Player 3</h2>
