            <option value="option 1">Option 1</option>
            <option value="option 2">Option 2</option>
          </select>

          <br></br>
          <br />


        </form>


        )
      }
    }
  }

  function Player4() {

    if (formCount <= 2) {
      return null;

    } else if (formCount === 3) {

      return (<p className="addClick" onClick={() => {
        addCount(formCount + 1)
      }}>+</p>
      )

    } else {

      if (props.type === "Players") {

        return (<form>
          <h2>Player 4</h2>
          <br />
          <label>Season: </label>
          <select value = {season4} onChange={evt => updateSeason4(evt.target.value)} name="selectList" id="selectList">
                  <option value="2020-2021">2020-2021</option>
                  <option value="2019-2020">2019-2020</option>
                  <option value="2018-2019">2018-2019</option>
                  <option value="2017-2018">2017-2018</option>
                  <option value="2016-2017">2016-2017</option>
                </select>

                <br></br>
                <br />

                <label>Team: </label>
                <select value = {selectedTeam4} onChange={evt => updateSelectedTeam4(evt.target.value)} name="selectList" id="selectList">
                  {teams4.map(team => (
                    <option>{team.name}</option>
                  ))}
                </select>

          <br></br>
          <br />

          <label>Player: </label>
          <select value = {selectedPlayer4} onChange={evt => updateSelectedPlayer4(evt.target.value)} name="selectList" id="selectList">
                  {players4.map(player => (
                    <option>{player.person.fullName}</option>
                  ))}
                </select>

        </form>)
      } else {
        return (<form>
          <h2>Team 4</h2>
          <br />
          <label>Season: </label>
          <select name="selectList" id="selectList" value={form1Option1} onChange={evt => updateF3O1(evt.target.value)}>
            <option value="option 1">Option 1</option>
            <option value="option 2">Option 2</option>
          </select>

          <br></br>
          <br />

          <label>Team: </label>
          <select name="selectList" id="selectList">
            <option value="option 1">Option 1</option>
            <option value="option 2">Option 2</option>
          </select>

          <br></br>
          <br />


        </form>


        )
      }

    }

  }



}

export default App;
