                <br />

                <label>Team: </label>
                <select name="selectList" id="selectList">
                  <option value="option 1">Option 1</option>
                  <option value="option 2">Option 2</option>
                </select>

                <br></br>
                <br />
              </form>

            </div>
            <div className="column">
              <Player3 />
            </div>
            <div className="column">
              <Player4 />
            </div>
          </div>

          <button className="submitButton">Submit</button>
        </div>
      </section>

    )


  }

  function Player3() {

    if (formCount <= 2) {
      return (
        <p className="addClick" onClick={() => {
          console.log(formCount);
          addCount(formCount + 1)
        }}>+</p>
      )
    } else {

      if (props.type === "Players") {

        return (<form>
          <h2>Player 3</h2>
          <br />
          <label>Season: </label>
          <select name="selectList" id="selectList" value={form1Option1} onChange={evt => updateF3O1(evt.target.value)}>
            <option value="option 1">Option 1</option>
            <option value="option 2">Option 2</option>
          </select>

          <br></br>
          <br />

          <label>Team: </label>
          <select name="selectList" id="selectList">
            <option value="option 1">Option 1</option>
            <option value="option 2">Option 2</option>
          </select>

          <br></br>
          <br />

          <label>Player: </label>
          <select name="selectList" id="selectList">
            <option value="option 1">Option 1</option>
            <option value="option 2">Option 2</option>
          </select>

        </form>)
      } else {
        return (<form>
          <h2>Team 3</h2>
          <br />
          <label>Season: </label>
          <select name="selectList" id="selectList" value={form1Option1} onChange={evt => updateF3O1(evt.target.value)}>
            <option value="option 1">Option 1</option>
            <option value="option 2">Option 2</option>
          </select>

          <br></br>
          <br />

          <label>Team: </label>
          <select name="selectList" id="selectList">
            <option value="option 1">Option 1</option>
            <option value="option 2">Option 2</option>
          </select>

          <br></br>
          <br />


        </form>


        )
      }
    }
  }

  function Player4() {

    if (formCount <= 2) {
      return null;

    } else if (formCount === 3) {

      return (<p className="addClick" onClick={() => {
        console.log(formCount);
        addCount(formCount + 1)
      }}>+</p>
      )

    } else {

      if (props.type === "Players") {

        return (<form>
          <h2>Player 4</h2>
          <br />
          <label>Season: </label>
          <select name="selectList" id="selectList" value={form1Option1} onChange={evt => updateF3O1(evt.target.value)}>
            <option value="option 1">Option 1</option>
            <option value="option 2">Option 2</option>
          </select>

          <br></br>
          <br />

          <label>Team: </label>
          <select name="selectList" id="selectList">
            <option value="option 1">Option 1</option>
            <option value="option 2">Option 2</option>
          </select>

          <br></br>
          <br />

          <label>Player: </label>
          <select name="selectList" id="selectList">
            <option value="option 1">Option 1</option>
            <option value="option 2">Option 2</option>
          </select>

        </form>)
      } else {
        return (<form>
          <h2>Team 4</h2>
          <br />
          <label>Season: </label>
          <select name="selectList" id="selectList" value={form1Option1} onChange={evt => updateF3O1(evt.target.value)}>
            <option value="option 1">Option 1</option>
            <option value="option 2">Option 2</option>
          </select>

          <br></br>
          <br />

          <label>Team: </label>
          <select name="selectList" id="selectList">
            <option value="option 1">Option 1</option>
            <option value="option 2">Option 2</option>
          </select>

          <br></br>
          <br />


        </form>


        )
      }

    }

  }

  function getTeams(season) {

    let fixedSeason = season.replace(/-/g,"");
    console.log(fixedSeason);
    async function fetchData() {



      await fetch(`https://statsapi.web.nhl.com/api/v1/teams?season=${fixedSeason}`, {
        method: "GET",


      })
        .then(res => res.json())
        .then(
          (result) => {

            updateTeams1(result.teams.sort(function(a, b){
              var nameA=a.name.toLowerCase(), nameB=b.name.toLowerCase()
              if (nameA < nameB) //sort string ascending
                  return -1 
              if (nameA > nameB)
                  return 1
              return 0 //default return value (no sorting)
          }));


          },


        )
    }

    fetchData();

    return;
  }


}

