import logo from './logo.svg';
import 'bulma/css/bulma.min.css';
import './App.css';
import headPic from './Stick.jpg';
import { useState } from 'react';

function App() {

  const [submitType, updateType] = useState('Players');
  const [color1, updateColor1] = useState('typeButton');
  const [color2, updateColor2] = useState('typeButton');

  return (
    <div>


      <div className="section">
        <div className="container">
          <h1>Hockey Comparison Tool<img className="headPic" src={headPic} alt="headPic" /></h1>
          <br />
          <br />
          <h2>Compare: <button className={color1} onClick={() => { updateType('Players'); updateColor1('typeButton2'); updateColor2('typeButton') }}><h2>Players</h2></button> <button className={color2} onClick={() => { updateType('Teams'); updateColor2('typeButton2'); updateColor1('typeButton') }}><h2>Teams</h2></button></h2>



        </div>
      </div>
      <Forms type={submitType} />
    </div>
  );
}

function Forms(props) {




  const [formCount, addCount] = useState(2);
  const [form1Option1, updateF3O1] = useState('option 2');
  const [formType, updateType] = useState(props.type);
  const [formObjs, updateObjs] = useState([{ prop1: '1', prop2: '2' }, { prop3: '3', prop4: '4' }])

  const [teams1, updateTeams1] = useState(null);

  if (formType !== props.type) {
    updateType(props.type);
    addCount(2);
    getTeams('2020-2021');

    let updatedObj = formObjs.map(item => {

      if (item.prop1 === '1') {
        return { ...item, prop1: '5' };
      }

      return item;
    })

    updateObjs(updatedObj);
  }

  console.log(formObjs);



  if (formType === 'Players') {



    return (


      <section>
        <div className='container has-background-grey-light'>
          <div className="columns">
            <div className="column">
              <form>
                <h2>Player 1</h2>
                <br />
                <label>Season: </label>
                <select onChange={evt => console.log(evt.target.value)} name="selectList" id="selectList">
                  <option>Select Season</option>
                  <option value="2020-2021">2020-2021</option>
                  <option value="2019-2020">2019-2020</option>
                  <option value="2018-2019">2018-2019</option>
                  <option value="2017-2018">2017-2018</option>
                </select>

                <br></br>
                <br />

                <label>Team: </label>
                <select name="selectList" id="selectList">

                </select>

                <br></br>
                <br />

                <label>Player: </label>
                <select name="selectList" id="selectList">
                  <option value="option 1">Option 1</option>
                  <option value="option 2">Option 2</option>
                </select>

              </form>

            </div>
            <div className="column">
              <form>
                <h2>Player 2</h2>
                <br />
                <label>Season: </label>
                <select name="selectList" id="selectList">
                  <option value="option 1">Option 1</option>
                  <option value="option 2">Option 2</option>
                </select>

                <br></br>
                <br />

                <label>Team: </label>
                <select name="selectList" id="selectList">
                  <option value="option 1">Option 1</option>
                  <option value="option 2">Option 2</option>
                </select>

                <br></br>
                <br />

                <label>Player: </label>
                <select name="selectList" id="selectList">
                  <option value="option 1">Option 1</option>
                  <option value="option 2">Option 2</option>
                </select>


              </form>

            </div>
            <div className="column">
              <Player3 />
            </div>
            <div className="column">
              <Player4 />
            </div>
          </div>

          <button className="submitButton">Submit</button>
        </div>
      </section>
    )

  } else {


    return (
      <section>
        <div className='container has-background-grey-light'>
          <div className="columns">
            <div className="column">
              <form>
                <h2>Team 1</h2>
                <br />
                <label>Season: </label>
                <select name="selectList" id="selectList">
                  <option value="option 1">Option 1</option>
                  <option value="option 2">Option 2</option>
                </select>

                <br></br>
                <br />

                <label>Team: </label>
                <select name="selectList" id="selectList">
                  <option value="option 1">Option 1</option>
                  <option value="option 2">Option 2</option>
                </select>

                <br></br>
                <br />

              </form>

            </div>
            <div className="column">
              <form>
                <h2>Team 2</h2>
                <br />
                <label>Season: </label>
                <select name="selectList" id="selectList">
                  <option value="option 1">Option 1</option>
                  <option value="option 2">Option 2</option>
                </select>

                <br></br>
                <br />

                <label>Team: </label>
                <select name="selectList" id="selectList">
                  <option value="option 1">Option 1</option>
                  <option value="option 2">Option 2</option>
                </select>

                <br></br>
                <br />
              </form>

            </div>
            <div className="column">
              <Player3 />
            </div>
            <div className="column">
              <Player4 />
            </div>
          </div>

          <button className="submitButton">Submit</button>
        </div>
      </section>

    )


  }

  function Player3() {

    if (formCount <= 2) {
      return (
        <p className="addClick" onClick={() => {
          console.log(formCount);
          addCount(formCount + 1)
        }}>+</p>
      )
    } else {

      if (props.type === "Players") {

        return (<form>
          <h2>Player 3</h2>
          <br />
          <label>Season: </label>
          <select name="selectList" id="selectList" value={form1Option1} onChange={evt => updateF3O1(evt.target.value)}>
            <option value="option 1">Option 1</option>
            <option value="option 2">Option 2</option>
          </select>

          <br></br>
          <br />

          <label>Team: </label>
          <select name="selectList" id="selectList">
            <option value="option 1">Option 1</option>
            <option value="option 2">Option 2</option>
          </select>

          <br></br>
          <br />

          <label>Player: </label>
          <select name="selectList" id="selectList">
            <option value="option 1">Option 1</option>
            <option value="option 2">Option 2</option>
          </select>

        </form>)
      } else {
        return (<form>
          <h2>Team 3</h2>
          <br />
          <label>Season: </label>
          <select name="selectList" id="selectList" value={form1Option1} onChange={evt => updateF3O1(evt.target.value)}>
            <option value="option 1">Option 1</option>
            <option value="option 2">Option 2</option>
          </select>

          <br></br>
          <br />

          <label>Team: </label>
          <select name="selectList" id="selectList">
            <option value="option 1">Option 1</option>
            <option value="option 2">Option 2</option>
          </select>

          <br></br>
          <br />


        </form>


        )
      }
    }
  }

  function Player4() {

    if (formCount <= 2) {
      return null;

    } else if (formCount === 3) {

      return (<p className="addClick" onClick={() => {
        console.log(formCount);
        addCount(formCount + 1)
      }}>+</p>
      )

    } else {

      if (props.type === "Players") {

        return (<form>
          <h2>Player 4</h2>
          <br />
          <label>Season: </label>
          <select name="selectList" id="selectList" value={form1Option1} onChange={evt => updateF3O1(evt.target.value)}>
            <option value="option 1">Option 1</option>
            <option value="option 2">Option 2</option>
          </select>

          <br></br>
          <br />

          <label>Team: </label>
          <select name="selectList" id="selectList">
            <option value="option 1">Option 1</option>
            <option value="option 2">Option 2</option>
          </select>

          <br></br>
          <br />

          <label>Player: </label>
          <select name="selectList" id="selectList">
            <option value="option 1">Option 1</option>
            <option value="option 2">Option 2</option>
          </select>

        </form>)
      } else {
        return (<form>
          <h2>Team 4</h2>
          <br />
          <label>Season: </label>
          <select name="selectList" id="selectList" value={form1Option1} onChange={evt => updateF3O1(evt.target.value)}>
            <option value="option 1">Option 1</option>
            <option value="option 2">Option 2</option>
          </select>

          <br></br>
          <br />

          <label>Team: </label>
          <select name="selectList" id="selectList">
            <option value="option 1">Option 1</option>
            <option value="option 2">Option 2</option>
          </select>

          <br></br>
          <br />


        </form>


        )
      }

    }

  }

  function getTeams(season) {

    let fixedSeason = season.replace(/-/g,"");
    console.log(fixedSeason);
    async function fetchData() {



      await fetch(`https://statsapi.web.nhl.com/api/v1/teams?season=${fixedSeason}`, {
        method: "GET",


      })
        .then(res => res.json())
        .then(
          (result) => {

            console.log(result.teams);


          },


        )
    }

    fetchData();

    return;
  }


}

export default App;
